mapper.select:
select * from product pr join category ca on pr.product_num = ca.product_num
                         join product_type pt on ca.type_num = pt.type_num
order by product_createDt desc limit 5;

select * from product pr join category ca on pr.product_num = ca.product_num
                         join product_type pt on ca.type_num = pt.type_num
order by rand() limit 1;

mapper.update:
update product set product_num = #{num}, product_title = #{title}, product_name = #{name}, product_price = #{price}, product_discount = #{discount},
                   inventory_quantity = #{quantity}, product_createDt = now(), delivery_info = #{delivery}, click_count = #{click}
where product_num = 12345

mapper.select:
select * from company co
         join category ca on ca.company_num = co.company_num
         where ca.product_num = #{productNum}

select * from product pr join category ca on pr.product_num = ca.product_num
                         join product_type pt on ca.type_num = pt.type_num
where pr.product_num = #{productNum}

select * from product pr join category ca on pr.product_num = ca.product_num
                         join product_type pt on ca.type_num = pt.type_num
where pt.type_num = #{typeNum}

select * from product pr join category ca on pr.product_num = ca.product_num join product_type pt on pt.type_num = ca.type_num join company cp on cp.company_num = ca.company_num join spec sp on sp.product_num = pr.product_num join part pa on pa.part_num = sp.part_num where (pr.product_title like concat('%', #{word}, '%') or pr.product_name like concat('%', #{word}, '%') or pa.part_name like concat('%', #{word}, '%') or pt.type_name like concat('%', #{word}, '%') or cp.company_name like concat('%', #{word}, '%')) and pr.product_price between #{minPrice} and #{maxPrice} and
cp.company_name = #{item}
group by pr.product_num;

select * from product pr join category ca on pr.product_num = ca.product_num join product_type pt on pt.type_num = ca.type_num join company cp on cp.company_num = ca.company_num join spec sp on sp.product_num = pr.product_num join part pa on pa.part_num = sp.part_num where (pr.product_title like concat('%', #{word}, '%') or pr.product_name like concat('%', #{word}, '%') or pa.part_name like concat('%', #{word}, '%') or pt.type_name like concat('%', #{word}, '%') or cp.company_name like concat('%', #{word}, '%')) and pr.product_price between #{minPrice} and #{maxPrice} and pt.type_name = #{type} and
cp.company_name = #{item}
group by pr.product_num;

select * from product pr join category ca on pr.product_num = ca.product_num join product_type pt on pt.type_num = ca.type_num join company cp on cp.company_num = ca.company_num join spec sp on sp.product_num = pr.product_num join part pa on pa.part_num = sp.part_num where (pr.product_title like concat('%', #{word}, '%') or pr.product_name like concat('%', #{word}, '%') or pa.part_name like concat('%', #{word}, '%') or pt.type_name like concat('%', #{word}, '%') or cp.company_name like concat('%', #{word}, '%')) and pr.product_price between #{minPrice} and #{maxPrice} and pr.product_discount > 0 and
cp.company_name = #{item}
group by pr.product_num;

select * from product pr join category ca on pr.product_num = ca.product_num join product_type pt on pt.type_num = ca.type_num join company cp on cp.company_num = ca.company_num join spec sp on sp.product_num = pr.product_num join part pa on pa.part_num = sp.part_num where (pr.product_title like concat('%', #{word}, '%') or pr.product_name like concat('%', #{word}, '%') or pa.part_name like concat('%', #{word}, '%') or pt.type_name like concat('%', #{word}, '%') or cp.company_name like concat('%', #{word}, '%')) and pr.product_price between #{minPrice} and #{maxPrice} and pt.type_name = #{type} and pr.product_discount > 0 and
cp.company_name = #{item}
group by pr.product_num;
